INSERT INTO movies (title, description, release_year, duration, director, country, language, genre)
VALUES
    ('The Matrix', 'A computer hacker learns from mysterious rebels about the true nature of his reality and his role in the war against its controllers.', 1999, 136, 'The Wachowskis', 'USA', 'English', 'Sci-Fi'),
    ('Inception', 'A thief who enters the dreams of others to steal secrets from their subconscious is given the task of planting an idea into the mind of a CEO.', 2010, 148, 'Christopher Nolan', 'USA', 'English', 'Sci-Fi'),
    ('The Shawshank Redemption', 'Two imprisoned men bond over a number of years, finding solace and eventual redemption through acts of common decency.', 1994, 142, 'Frank Darabont', 'USA', 'English', 'Drama');
INSERT INTO series (title, description, release_year, number_of_seasons, number_of_episodes, creator, country, language, genre)
VALUES
    ('Breaking Bad', 'A high school chemistry teacher turned methamphetamine producer partners with a former student to build a meth empire.', 2008, 5, 62, 'Vince Gilligan', 'USA', 'English', 'Crime'),
